# taxonomy.py
import re

def parse_ifrs_tree_string(tree_string: str) -> dict:
    """Парсит строку с иерархией МСФО в словарь."""
    taxonomy = {}
    lines = tree_string.strip().split('\n')
    for line in lines:
        if ':' not in line:
            continue
        parent, children_str = line.split(':', 1)
        parent = parent.strip()
        
        # Используем regex для правильного разделения с учетом знаков
        children = re.findall(r'([+-]?)(\w+)', children_str)
        
        if parent not in taxonomy:
            taxonomy[parent] = []
            
        formula = []
        for sign, child in children:
            child = child.strip()
            # Если знака нет, по умолчанию ставим +
            sign_val = -1 if sign == '-' else 1
            formula.append({'child': child, 'sign': sign_val})
        
        # Добавляем новую формулу, если она еще не существует
        # (некоторые статьи имеют несколько определений)
        if formula not in taxonomy[parent]:
             taxonomy[parent].append(formula)
             
    # Преобразуем структуру: {'Parent': [[formula1], [formula2]]}
    final_taxonomy = {}
    for parent, formulas in taxonomy.items():
        # Сортируем формулы по количеству компонентов (предпочитаем более детальные)
        sorted_formulas = sorted(formulas, key=len, reverse=True)
        final_taxonomy[parent] = sorted_formulas
        
    return final_taxonomy

