IFRS_TAXONOMY = parse_ifrs_tree_string(IFRS_TREE_CONTENT)

def find_parent(child_item: str, taxonomy: dict) -> list:
    """Находит все родительские статьи для заданного компонента."""
    parents = []
    for parent, formulas in taxonomy.items():
        for formula in formulas:
            for component in formula:
                if component['child'] == child_item:
                    parents.append(parent)
                    break
    return list(set(parents))